import React from 'react';
import s from "./OneBook.module.css"
import {useState} from "react";
import Counter from "../Counter/Counter";

const OneBook = ({info}) => {
    // const [count, setCount] = useState(0)
    const get_stars = (num) => {
        let stars = ""
        if (num > 5) {
            num = 5
        }
        for (let i = 0; i < num; i++){
            stars += "★ "
        }
        let n = 5 - num
        for (let i = 1; i <= n; i++){
            stars += "☆ "
        }
        return stars
    }

    // const min_count = () => {
