import React, {useContext, useState} from 'react';
import s from './GenersBar.module.css'
import {InfoContext} from "../../context/InfoContext";
import classNames from "classnames";

const GenresBar = ({books}) => {
    const current_genrer = useContext(InfoContext)
    const [current, setCurrent] = useState(current_genrer)


    const genres = []
    books.map((item) => {
        if (genres.indexOf(item.type) === -1){
            genres.push(item.type)
        }
        return 1
    })
    let elements = genres.map((item) => {
        console.log(item.genre_text)
        if (item.genre_text === current) {
            return (
                <h3 className={classNames(s.genre_main, s.active_genrer)} key={item}>
                    <span className={s.genre_text}>{item}</span>
                </h3>
            )
        }
        return (
            <h3 className={classNames(s.genre_main)} key={item}>
                <span className={s.genre_text}>{item}</span>
            </h3>
        )
    })
    return (
        <div className={s.genres_bar}>
            {elements}
        </div>
    );
};

export default GenresBar;
