import React, {useEffect, useState} from 'react';
import s from "./Total.module.css"
import ShortBook from "../ShortBook/ShortBook";
import {useSelector} from "react-redux";
import {selectBookModule} from "../../store/book/selectors";
import {selectCartBooks} from "../../store/cart/selector";

const Total = ({books, counters}) => {

    const books_selected = useSelector((state) => selectBookModule(state));
    console.log(books_selected['entities'])
    console.log("counters", counters)

    // useEffect(() => {
    //     let price = 0;
    //     Object.keys(cart).map((key, index) => {
    //         console.log(cart[key])
    //         let val = cart[key];
    //         price += books_selected.entities[key].price * val;
    //     });
    //     console.log(price)
    // }, [])

    let book_list = books.map((item) => {
        return (
            <ShortBook bookId={item} key={item}/>
        )
    })

    return (
        <div className={s.total}>
            <div className={s.content}>
                <h1 className={s.title}>Ваш заказ:</h1>
                <div className={s.book_list}>
                    {book_list}
                </div>
                <div className={s.bottom}>
                    <div className={s.bottom_content}>
                        <p className={s.itog}>Итого: <span>{"1000"}₽</span></p>
                        <button className={s.buy}>Купить</button>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default Total;
