import React from 'react';
import s from "./CartCards.module.css"
import OneBook from "../OneBook/OneBook";
import {useDispatch, useSelector} from "react-redux";
import {selectBooksCounters} from "../../store/cart/selector";

const CartCards = () => {

    const dispatch = useDispatch()
    const counters = useSelector((state) => selectBooksCounters(state))
    const books = Object.keys(counters)

    let elems = books.map((item) => {
        return (
            <OneBook id={item} key={item} isCard={true}/>
        )
    })

    return (
        <div className={s.cards}>
            {elems}
        </div>
    );
};

export default CartCards;
